sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var change_detected = false;
	var total_samples = 0;
	var ids = '';
	//TMB Negative value
	if(current.positive_sample_type.changesTo('tmb') || (current.positive_sample_type == 'tmb' && current.positive_sample_number.changes())  ){

		current.positive_sample_number = current.positive_sample_number * 3;
		change_detected = true;
	}

	if(current.negative_sample_type.changesTo('tmb') || (current.negative_sample_type == 'tmb' && current.negative_sample_number.changes())  ){

		current.negative_sample_number = current.negative_sample_number * 3;
		change_detected = true;
	}


	if(current.positive_sample_number.changes() || current.negative_sample_number.changes() || current.sample_number.changes()
		|| current.positive_sample_ids.changes() || current.negative_sample_ids.changes() || current.sample_ids.changes() ){

		change_detected = true;
	}


	//Get all tests and total samples and IDs

	var req = new GlideRecord('x_exte_cml_request_cml_request');
	req.get(current.request_number);


	if(change_detected == true){
		// Get the request record


		//get all the tests relating to the request
		var tests = new GlideRecord('x_exte_cml_request_cml_request_tests');
		tests.addQuery('request_number',current.request_number);
		tests.addQuery('number','!=',current.number);
		tests.query();




		//loop thru all tests retreving samples
		while(tests.next()){

			total_samples = total_samples + parseInt(tests.positive_sample_number)+ parseInt(tests.negative_sample_number) +parseInt(tests.sample_number);

			ids = ids+' '+ tests.positive_sample_ids+' ' +tests.negative_sample_ids+ ' ' +tests.sample_ids;


		}


		total_samples = total_samples + current.positive_sample_number + current.negative_sample_number+current.sample_number;
		ids = ids+' '+ current.positive_sample_ids+' ' +current.negative_sample_ids+ ' ' +current.sample_ids;




	}

	var state = req.state;
	var priority = req.priority;
	if(state < 25){
		if(((parseInt(priority) < parseInt(total_samples)) && parseInt(priority) != 0 ) || priority == 3 )
			{
			req.approval_required = true;
			gs.addInfoMessage('Director level approval required');
		}else{
			req.approval_required = false;
			gs.addInfoMessage('Director level approval NOT required');
		}

	}
	req.sample_ids = ids;
	req.number_of_samples = total_samples;
	req.update();




})(current, previous);
