sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	g_form.setValue('positive_standard_number','');
	g_form.setValue('negative_standard_number','');
	g_form.setValue('positive_other_number','');
	g_form.setValue('negative_other_number','');
	g_form.setValue('positive_top_middle_bottom_number','');
	g_form.setValue('negative_top_middle_bottom_number','');

	g_form.setValue('positve_standard_ids','');
	g_form.setValue('negative_standard_ids','');
	g_form.setValue('positive_other_ids','');
	g_form.setValue('negative_other_ids','');
	g_form.setValue('positive_top_middle_bottom_ids','');
	g_form.setValue('negative_top_middle_bottom_ids','');


	g_form.setValue('sample_number','');
	g_form.setValue('sample_ids','');
	//g_form.setValue('negative_sample_ids','');
	//g_form.setValue('positive_sample_ids','');


}
