sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var ga = new GlideAjax('CMLTestResults');
	ga.addParam('sysparm_name','getTestCount');
	ga.addParam('sysparm_number',g_form.getUniqueValue());
	ga.getXML(processResp);

	function processResp(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var priority = g_form.getValue('priority');
		if((priority < answer) && priority != 0){
			g_form.addErrorMessage('The number of tests exceeds the permitted amount for the priority you tried to select.');
			g_form.setValue('priority',oldValue);
		}

	}
}
