sys_script_include.script:
var CreateTestRecord = Class.create();
CreateTestRecord.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    //type: 'CreateTestRecord'
	createTestRecord:function(){
		var testID = this.getParameter('sysparm_testid');
		var reqnum = this.getParameter('sysparm_reqnum');
		var tst = new GlideRecord('x_exte_cml_request_cml_tests');
		tst.get(testID);

		var req_tst = new GlideRecord('x_exte_cml_request_cml_request_tests');
		req_tst.initialize();
		req_tst.request_number = reqnum;
		req_tst.test_name = tst.test_name;
		req_tst.insert();
		return 'Hello ' +tst.test_name;



	}
});
